angular.module("shoppinpal.mobile-menu",[])
	.run([
		"$rootScope",
		"$spMenu",
		function(a,b){a.$spMenu=b}])
		.provider(

			"$spMenu",function(){
				this.$get=[
					function(){
						var a={};

						return a.show=function(){
								
							
								var a=angular.element(document.querySelector("#sp-nav"));
								console.log(a),a.addClass("show")
							},a.hide=function(){
								var a=angular.element(document.querySelector("#sp-nav"));
								a.removeClass("show")
							},a.toggle=function(){
								var a=angular.element(document.querySelector("#sp-nav"));
								//angular.element(document.querySelector("#sp-nav")).style['-webkit-transition-duration'] = '0.3s';
								//angular.element(document.querySelector("#sp-nav")).style['transition-duration'] = '0.3s';
								a.toggleClass("show")
						},a
					}
				]
			}
		);